import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Sidebar.css';

function Sidebar() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || 'null');

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <aside className="app-sidebar">
      <div className="sidebar-top">
        <div className="brand">Task Manager</div>
      </div>

      <nav className="sidebar-nav">
        <Link to="/dashboard" className="sidebar-link">üè† Dashboard</Link>
        <Link to="/tasks" className="sidebar-link">üìù Tasks</Link>
        <Link to="/calendar" className="sidebar-link">üìÖ Calendar</Link>
        <Link to="/settings" className="sidebar-link">‚öôÔ∏è Settings</Link>
      </nav>

      <div className="sidebar-bottom">
        <div className="sidebar-user">
          <div className="sidebar-avatar">{user && user.name ? user.name.charAt(0).toUpperCase() : 'U'}</div>
          <div className="sidebar-username">{user ? user.name : 'User'}</div>
        </div>

        <button className="sidebar-logout" onClick={handleLogout}>Logout</button>
      </div>
    </aside>
  );
}

export default Sidebar;
