import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Dashboard.css';

// Small SVG donut chart component (no external deps)
function DonutChart({ completed = 0, pending = 0, size = 260, stroke = 36 }) {
  const total = completed + pending;
  const radius = Math.floor(size / 2);
  const normalizedRadius = radius - stroke / 2;
  const circumference = normalizedRadius * 2 * Math.PI;
  const completedPortion = total === 0 ? 0 : completed / total;
  const dash = circumference * completedPortion;

  return (
    <div className="donut-wrap" style={{ width: size }}>
      {total === 0 ? (
        <div className="donut-empty">No data</div>
      ) : (
        <svg height={size} width={size} className="donut-svg">
          <circle
            stroke="#e6eef8"
            fill="transparent"
            strokeWidth={stroke}
            r={normalizedRadius}
            cx={radius}
            cy={radius}
          />
          <circle
            stroke="#16a34a"
            fill="transparent"
            strokeWidth={stroke}
            strokeLinecap="round"
            strokeDasharray={`${dash} ${Math.max(0, circumference - dash)}`}
            r={normalizedRadius}
            cx={radius}
            cy={radius}
            transform={`rotate(-90 ${radius} ${radius})`}
          />

          <text x="50%" y="50%" textAnchor="middle" dy="6" className="donut-text">
            {Math.round(completedPortion * 100)}%
          </text>
        </svg>
      )}
    </div>
  );
}

function Dashboard() {
  const [stats, setStats] = useState({
    totalTasks: 0,
    pendingTasks: 0,
    completedTasks: 0,
    overdueTasks: 0
  });
  const [recentTasks, setRecentTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/tasks');
      const data = await response.json();
      
      const totalTasks = data.length;
      const completedTasks = data.filter(t => t.status === 'completed').length;
      const pendingTasks = data.filter(t => t.status === 'pending').length;
      const overdueTasks = data.filter(t => t.status === 'overdue' || (t.dueDate && new Date(t.dueDate) < new Date() && t.status !== 'completed')).length;
      
      setStats({
        totalTasks,
        pendingTasks,
        completedTasks,
        overdueTasks
      });
      
      setRecentTasks(data.slice(0, 5));
    } catch (err) {
      console.error('Failed to fetch dashboard data:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Welcome, {user.name || 'User'}!</h1>
        <p>Here's your task overview</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Tasks</h3>
          <p className="stat-value">{stats.totalTasks}</p>
        </div>

        <div className="stat-card completed">
          <h3>Completed</h3>
          <p className="stat-value">{stats.completedTasks}</p>
        </div>

        <div className="stat-card pending">
          <h3>Pending Tasks</h3>
          <p className="stat-value">{stats.pendingTasks}</p>
        </div>

        <div className="stat-card overdue">
          <h3>Overdue</h3>
          <p className="stat-value">{stats.overdueTasks}</p>
        </div>
      </div>

      <div className="donut-row">
        <div className="donut-column">
          <div className="donut-card">
            <h3>Task Distribution</h3>
            <DonutChart completed={stats.completedTasks} pending={stats.pendingTasks} size={320} stroke={48} />
            <div className="donut-legend">
              <div><span className="legend-dot completed"></span> Completed</div>
              <div><span className="legend-dot pending"></span> Pending</div>
            </div>
          </div>
        </div>
        <div className="chart-column">
          <div className="donut-card">
            <h3>Task Completion Rate</h3>
            <svg width="100%" height="300" style={{ marginTop: '10px' }}>
              <line x1="10" y1="250" x2="100%" y2="250" stroke="#ddd" strokeWidth="1" />
              <polyline points="20,200 40,180 60,190 80,120 100,140 120,80 140,100" stroke="#2563eb" strokeWidth="3" fill="none" />
              <circle cx="20" cy="200" r="4" fill="#2563eb" />
              <circle cx="40" cy="180" r="4" fill="#2563eb" />
              <circle cx="60" cy="190" r="4" fill="#2563eb" />
              <circle cx="80" cy="120" r="4" fill="#2563eb" />
              <circle cx="100" cy="140" r="4" fill="#2563eb" />
              <circle cx="120" cy="80" r="4" fill="#2563eb" />
              <circle cx="140" cy="100" r="4" fill="#2563eb" />
            </svg>
          </div>
        </div>
      </div>

      <div className="recent-tasks-section">
        <h2>Recent Tasks</h2>
        
        {loading && <p>Loading...</p>}
        
        {recentTasks.length === 0 && !loading && (
          <p className="no-tasks-message">No tasks yet. Create one to get started!</p>
        )}

        {recentTasks.length > 0 && (
          <div className="tasks-list">
            {recentTasks.map(task => (
              <div key={task._id} className={`task-item status-${task.status}`}>
                <div className="task-info">
                  <h3>{task.title}</h3>
                  <p>{task.description || 'No description'}</p>
                  <div className="task-details">
                    <span className={`status-badge status-${task.status}`}>
                      {task.status}
                    </span>
                    {task.dueDate && (
                      <span className="due-date">
                        üìÖ Due: {new Date(task.dueDate).toLocaleDateString()}
                      </span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="quick-actions">
        <h2>Quick Actions</h2>
        <div className="actions-grid">
          <Link to="/tasks" className="action-card">
            <span className="action-icon">üìù</span>
            <h3>Manage Tasks</h3>
          </Link>
          <Link to="/calendar" className="action-card">
            <span className="action-icon">üìÖ</span>
            <h3>View Calendar</h3>
          </Link>
          <Link to="/settings" className="action-card">
            <span className="action-icon">‚öôÔ∏è</span>
            <h3>Settings</h3>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
