import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Dashboard.css';

// Donut chart showing completed (green) and pending (light blue) as two arcs
function DonutChart({ completed = 0, pending = 0, size = 260, stroke = 36 }) {
  const total = completed + pending;
  const radius = Math.floor(size / 2);
  const normalizedRadius = radius - stroke / 2;
  const circumference = normalizedRadius * 2 * Math.PI;
  const completedPortion = total === 0 ? 0 : completed / total;
  const completedDash = circumference * completedPortion;
  const pendingDash = circumference * (pending / total);

  return (
    <div className="donut-wrap" style={{ width: size }}>
      {total === 0 ? (
        <div className="donut-empty">No data</div>
      ) : (
        <svg height={size} width={size} className="donut-svg">
          {/* Completed arc (green) */}
          <circle
            stroke="#10b981"
            fill="transparent"
            strokeWidth={stroke}
            strokeLinecap="round"
            strokeDasharray={`${completedDash} ${circumference}`}
            r={normalizedRadius}
            cx={radius}
            cy={radius}
            transform={`rotate(-90 ${radius} ${radius})`}
          />
          
          {/* Pending arc (light blue) */}
          <circle
            stroke="#93c5fd"
            fill="transparent"
            strokeWidth={stroke}
            strokeLinecap="round"
            strokeDasharray={`${pendingDash} ${circumference}`}
            r={normalizedRadius}
            cx={radius}
            cy={radius}
            transform={`rotate(${-90 + (completedPortion * 360)} ${radius} ${radius})`}
          />

          <text x="50%" y="50%" textAnchor="middle" dy="6" className="donut-text">
            {Math.round(completedPortion * 100)}%
          </text>
        </svg>
      )}
    </div>
  );
}

function Dashboard() {
  const [stats, setStats] = useState({
    totalTasks: 0,
    pendingTasks: 0,
    completedTasks: 0,
    overdueTasks: 0
  });
  const [recentTasks, setRecentTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/tasks');
      const data = await response.json();
      
      const totalTasks = data.length;
      const completedTasks = data.filter(t => t.status === 'completed').length;
      const pendingTasks = data.filter(t => t.status === 'pending').length;
      const overdueTasks = data.filter(t => t.status === 'overdue' || (t.dueDate && new Date(t.dueDate) < new Date() && t.status !== 'completed')).length;
      
      setStats({
        totalTasks,
        pendingTasks,
        completedTasks,
        overdueTasks
      });
      
      setRecentTasks(data.slice(0, 5));
    } catch (err) {
      console.error('Failed to fetch dashboard data:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard-container">
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Tasks</h3>
          <p className="stat-value">{stats.totalTasks}</p>
        </div>

        <div className="stat-card completed">
          <h3>Completed</h3>
          <p className="stat-value">{stats.completedTasks}</p>
        </div>

        <div className="stat-card pending">
          <h3>Pending Tasks</h3>
          <p className="stat-value">{stats.pendingTasks}</p>
        </div>

        <div className="stat-card overdue">
          <h3>Overdue</h3>
          <p className="stat-value">{stats.overdueTasks}</p>
        </div>
      </div>

      <div className="donut-row">
        <div className="donut-column">
          <div className="donut-card">
            <h3>Task Distribution</h3>
            <DonutChart completed={stats.completedTasks} pending={stats.pendingTasks} size={320} stroke={48} />
            <div className="donut-legend">
              <div><span className="legend-dot completed"></span> Completed</div>
              <div><span className="legend-dot pending"></span> Pending</div>
            </div>
          </div>
        </div>
        <div className="chart-column">
          <div className="donut-card">
            <h3>Task Completion Rate</h3>
            <svg width="100%" height="300" viewBox="0 0 400 300" style={{ marginTop: '10px' }}>
              {/* Grid lines */}
              <line x1="30" y1="20" x2="30" y2="260" stroke="#e5e7eb" strokeWidth="1" />
              <line x1="30" y1="260" x2="390" y2="260" stroke="#e5e7eb" strokeWidth="1" />
              {/* Y-axis labels */}
              <text x="10" y="265" fontSize="11" fill="#666">0</text>
              <text x="10" y="185" fontSize="11" fill="#666">1.0</text>
              <text x="10" y="105" fontSize="11" fill="#666">2.0</text>
              <text x="10" y="25" fontSize="11" fill="#666">4.0</text>
              {/* X-axis labels */}
              <text x="50" y="280" fontSize="11" fill="#666">Mon</text>
              <text x="110" y="280" fontSize="11" fill="#666">Tue</text>
              <text x="170" y="280" fontSize="11" fill="#666">Wed</text>
              <text x="230" y="280" fontSize="11" fill="#666">Thu</text>
              <text x="290" y="280" fontSize="11" fill="#666">Fri</text>
              {/* Line chart */}
              <polyline points="50,200 110,160 170,180 230,80 290,120 350,40" stroke="#2563eb" strokeWidth="3" fill="none" />
              {/* Data points */}
              <circle cx="50" cy="200" r="4" fill="#2563eb" />
              <circle cx="110" cy="160" r="4" fill="#2563eb" />
              <circle cx="170" cy="180" r="4" fill="#2563eb" />
              <circle cx="230" cy="80" r="4" fill="#2563eb" />
              <circle cx="290" cy="120" r="4" fill="#2563eb" />
              <circle cx="350" cy="40" r="4" fill="#2563eb" />
            </svg>
          </div>
        </div>
      </div>

      <div className="recent-tasks-section">
        <h2>Recent Tasks</h2>
        
        {loading && <p>Loading...</p>}
        
        {recentTasks.length === 0 && !loading && (
          <p className="no-tasks-message">No tasks yet. Create one to get started!</p>
        )}

        {recentTasks.length > 0 && (
          <div className="tasks-list">
            {recentTasks.map(task => (
              <div key={task._id} className={`task-item status-${task.status}`}>
                <div className="task-info">
                  <h3>{task.title}</h3>
                  <p>{task.description || 'No description'}</p>
                  <div className="task-details">
                    <span className={`status-badge status-${task.status}`}>
                      {task.status}
                    </span>
                    {task.dueDate && (
                      <span className="due-date">
                        üìÖ Due: {new Date(task.dueDate).toLocaleDateString()}
                      </span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="quick-actions">
        <h2>Quick Actions</h2>
        <div className="actions-grid">
          <Link to="/tasks" className="action-card">
            <span className="action-icon">üìù</span>
            <h3>Manage Tasks</h3>
          </Link>
          <Link to="/calendar" className="action-card">
            <span className="action-icon">üìÖ</span>
            <h3>View Calendar</h3>
          </Link>
          <Link to="/settings" className="action-card">
            <span className="action-icon">‚öôÔ∏è</span>
            <h3>Settings</h3>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
